-- DROP TABLE IF EXISTS finance_monthly;
CREATE TABLE finance_monthly (
    id INT AUTO_INCREMENT PRIMARY KEY,
    lokasi_id INT,
    period DATE,
    pendapatan DECIMAL(15,2),
    pengeluaran DECIMAL(15,2),
    FOREIGN KEY (lokasi_id) REFERENCES lokasi(id)
);

